classes.content:
self .FdVirtualGrid > div .selected { 
	background-color: transparent;
}
self .FdVirtualGrid > div :hover{
	background: transparent;
}

self .fd-selectbox__toggle_button, self .fd-input, self input{
	border: 1px solid red;
}

self >label>span{
	color: red;
}

self {
  border-radius: 6px;
  box-shadow: 0px 4px 16px 0px rgba(0, 0, 0, 0.12);
}

self .header .header-cell {
	background-color: #FFFFFF;
	border-bottom: solid 1 px #E2E8F0;
	color: #ACB8C8;
	font-size: 18px;
	font-weight: 500;
	padding: 1.25rem 2rem;
}

self .row {
  	align-items: center;
  	color: #353D66;
	font-size: 16px;
	font-weight: 500;
}

self .row .col .w-full{
    overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;
}

self .row.selected {
  	background-color: #E2E8F0 !important;
}

self .col-even {
	font-weight: 400 !important;
}

self .col-even~.col {
	font-weight: 400 !important;
}
self .row .cell {
  padding: 0.5rem 2rem;
}

self .innerScrollContainer .row {
    border-width: 1px !important;
}

self:has(span[title="to be approved"]) {
	border-bottom: solid 3px #ECB22E !important;
}

self:has(span[title="approved"]) {
	border-bottom: solid 3px #119A8D !important;
}

self:has(span[title="rejected"]) {
	border-bottom: solid 3px #F44C4C !important;
}

self {
	display: -webkit-box;
    -webkit-box-orient: vertical;
    overflow: hidden;
    -webkit-line-clamp: 4; /* Restrict to two(3) lines */
	text-overflow: ellipsis;
    max-width: 100%;
}
